import { React,useEffect, useContext, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom'; 
import noteContext from '../noteContext';
import './profile.css'
import ListItemIcon from '@mui/material/ListItemIcon';
import HomeIcon from '@mui/icons-material/Home';
const Profile = () => {
    
    const navigate = useNavigate();
    const context = useContext(noteContext);
    const { isLogined, userdetails } = context;
    const [logged, setLogged] = useState(false);
    const [user, setuser] = useState("");  
    useEffect(() => {
      const checkLoginStatus = async () => {
        const loggedIn = await isLogined();
        setLogged(loggedIn);
         console.log(loggedIn)
        if (loggedIn) {
          const userinfo = await userdetails();
            setuser(userinfo);
            console.log(userinfo)
        }
        else{
            navigate('/');
        }
      };
      checkLoginStatus();
    }, [isLogined]);
  return (
    <> 
      <div className='profilemain'>
        <div className="homelink">
              <div className="fs-1 ms-5 mt-2 fw-medium">My Profile</div>
              <Link to="/">
                        <ListItemIcon style={{minWidth:"1px"}}>
                          <HomeIcon fontSize="small" />
                        </ListItemIcon>
                       Home
             </Link>
        </div>
        <div className='containers d-flex'>
                <div className='profileleft'>
                       <div className='profilepic'></div>
                       <button className='editprofile'>Edit profile</button>
                </div>
                <div className='profileright'>
                       <div className="name">
                             <p>Full Name</p><span>{user.name}</span>
                       </div>
                       <div className="email">
                             <p>Email</p><span>{user.email}</span>
                       </div>
                       <div className="orders">My Orders</div>
                       <div className="changepassword">Change Password</div>
                       
                </div>
        </div>
      </div> 
    </>
  )
}

export default Profile
