import {React,useEffect,useContext} from 'react'
import Carousel from '../carousel'
import './product.css'
import noteContext from '../noteContext'
import { useParams } from 'react-router-dom';
import Spinner from '../spinner';
import ReactStars from 'react-rating-stars-component'




const Productitem = (props) => {
    const context=useContext(noteContext)
    const {showproductdetail,product,isLoading}=context;
    const {id}=useParams()

    const option={
        edit:false,
        color:"rgba(20,20,20,0.1)",
        activeColor:"tomato",
        value: product.rating || 0,
        isHalf:true,
        size: "30"
      }


    const fetchproductdetail=async()=>{
        await showproductdetail(id)
      
    }

    useEffect(()=>{
        fetchproductdetail()
    },[])

  return (
    <>
        { isLoading ? (   <Spinner/> ) : (
            <div className="productitempage">
                <div className="productitembox container">
                      <div className="carouselbox">
                               <Carousel image={product.image}/>
                        
                      </div>
                      
                     <div className="productarea">
                          <div className="productname">
                                <h2>{product.name}</h2>
                                <span>Product  #{product._id}</span>
                          </div>
                          <div className="productrating">
                               <div><ReactStars {...option} /><span>{`( ${product.totalreview}  Reviews )`}</span></div>

                          </div>

                          <div className="productprice">
                                 â‚¹{product.price}
                          </div>
                          <div className="productorder">
                                  <div className="productcount">
                                        <div>-</div>
                                        
                                  </div>
                                  <div className="productcart"></div>

                          </div>

                    </div >
                </div>
        
            </div>
        )}
    </>
  )
}

export default Productitem
