import * as React from 'react';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { useState,useContext } from 'react';
import noteContext from '../noteContext';
import { Link,useNavigate } from 'react-router-dom';
import Alert from '../elements/alert';
import Setavatar from '../setAvatar';
import { red } from '@mui/material/colors';
import './profile.css'



const Editprofile=()=> {
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
    const context = useContext(noteContext);
    const {userSignup,alert,callalert}=context;
    const navigate= useNavigate();
    const [selectedimage,setimage]=useState('');
    const [node, setnode] = useState({ name: "", email: "",avatar:{image_id:"sampltid",url:"/images/randomprofile.png"}});
    
    const handleChange = (e) => {
        setnode({ ...node, [e.target.name]: e.target.value });
    };
    const handleimage = () => {
        setnode({ ...node,  avatar: {
            url: selectedimage, 
            image_id: selectedimage 
        }});
    };
   

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (node.name.length < 4)  return callalert("Invalid Name", "Name should be at least 3 characters long");
        if (!node.avatar.url)      return callalert("Invalid image", "image failed");
        const value=await userSignup(node)
        if(value)  navigate('/login')

        
    };

  return (
    <div>
      <Button className='editprofile' onClick={handleOpen}>Edit Profile</Button>
      <Modal
        keepMounted
        open={open}
        onClose={handleClose}
        aria-labelledby="keep-mounted-modal-title"
        aria-describedby="keep-mounted-modal-description"
      >
        
              
      </Modal>
    </div>
  );
}
export default Editprofile;





















   

