import * as React from 'react';
import { useEffect, useContext, useState } from 'react';
import { useNavigate,Link } from 'react-router-dom'; 
import noteContext from '../noteContext';
import Button from '@mui/material/Button';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import PopupState, { bindTrigger, bindMenu } from 'material-ui-popup-state';

export default function MenuPopupState() {
  const navigate = useNavigate();
  const context = useContext(noteContext);
  const { isLogined, userLogout } = context;
  const [logged, setlogged] = useState(false);

  useEffect(() => {
    const checkLoginStatus = async () => {
      const loggedIn = await isLogined();
      setlogged(loggedIn);
    };
    checkLoginStatus();
  }, [isLogined]);

  const logout = async () => {
    await userLogout();
    setlogged(false);
    navigate('./');


            // {!logged? <><Link className="btn btn-primary mx-2" to="http://localhost:3000/login" role="button">Login</Link>
        //     <Link className="btn btn-primary" to="http://localhost:3000/signup" role="button">SignUp</Link></>:
        //     <button className="btn btn-primary mx-2" onClick={logout}>Logout</button>
        //     }
  };

  return (
    <PopupState variant="popover" popupId="demo-popup-menu">
      {(popupState) => (
        <React.Fragment>
          <Button variant="contained" {...bindTrigger(popupState)}>
            Dashboard
          </Button>
          <Menu
            {...bindMenu(popupState)}
            PaperProps={{
              style: {
                minWidth: '130px',
                padding: '10px',
              },
            }}
          >
            {logged && <MenuItem 
                              component={Link}
                              to="http://localhost:3000/login" 
                             onClick={popupState.close} >
                            Profile
                        </MenuItem>}
            {logged &&  <MenuItem onClick={popupState.close} > 
                                 Orders 
                        </MenuItem>}
            {logged && <MenuItem 
                            onClick={() => {
                             popupState.close();
                            logout();
              }} 
            > Logout </MenuItem>}
            {!logged && <MenuItem
              component={Link}
              to="http://localhost:3000/login"   // Link to the login page
              onClick={popupState.close}
            >
              Login
            </MenuItem>}
            {!logged && <MenuItem
              component={Link}
              to="http://localhost:3000/signup"   // Link to the login page
              onClick={popupState.close}
            >
              Signup
            </MenuItem>}
          </Menu>
        </React.Fragment>
      )}
    </PopupState>
  );
}
