import noteContext from "./noteContext";
import React ,{useState} from 'react'



const NoteState = (props) => {


      // API call for register
      const userSignup=async()=>{

          try {
              const response = await fetch('http://localhost:5000/api/auth/signup', {
                  method: 'POST',
                  headers: {
                      'Content-Type': 'application/json'
                  },
                  body: JSON.stringify({
                      name: node.name,
                      email: node.email,
                      password: node.password
                  })
              });

              const data = await response.json();

              if (!response.ok)      return props.callalert("Error", data.error || "Something went wrong");

              props.callalert("Success", "Registration successful");
             // navigate('/setavatar');
              // Handle success (e.g., redirect to login page or show success message)
          } catch (error) {
              console.error('Error:', error);
              props.callalert("Error", "Server error, please try again later");
          }

      } 


  return (
    <noteContext.Provider value={{}}>
            {props.children}
    </noteContext.Provider>
  )
}

export default NoteState
