import {React,useEffect,useContext,useState} from 'react'
import {Link,useLocation,useNavigate} from "react-router-dom"; 
import noteContext from './noteContext';
import MenuPopupState from './elements/usermenu';



const Navbar = () => {
    const navigate=useNavigate();
    const context=useContext(noteContext)
    const {callalert,isLogined,userLogout}=context;
    const [keyword,setkeyword]=useState('')
    const location=useLocation();

     useEffect(()=>{
        
    },[])

     const searchproduct=()=>{
        navigate(`/searchproducts/${keyword.trim()}`)
     }

  return (
    <>
      
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <Link className="navbar-brand " to="/">Navbar</Link>
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="/navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>

            <div className="collapse navbar-collapse " id="navbarSupportedContent">
                <ul className="navbar-nav mr-auto">
                <li className="nav-item active mx-2">
                    <Link className={`nav-Link  font-weight-${location.pathname==="/"? "bold":""}`} to="/">Home <span className="sr-only">(current)</span></Link>
                </li>
                <li className="nav-item mx-2">
                    <Link className={`nav-Link  font-weight-${location.pathname==="/about"? "bold":""}`} to="/about">About</Link>
                </li>
                <li className="nav-item dropdown mx-2">
                    <Link className="nav-Link dropdown-toggle " to="/" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                    Dropdown
                    </Link>
                    <div className="dropdown-menu" aria-labelledby="navbarDropdown">
                    <Link className="dropdown-item" to="/">Action</Link>
                    <Link className="dropdown-item" to="/">Another action</Link>
                    <div className="dropdown-divider"></div>
                    <Link className="dropdown-item" to="/">Something else here</Link>
                    </div>
                </li>
                <li className="nav-item mx-2">
                    <Link className="nav-Link disabled " to="/">Disabled</Link>
                </li>
                </ul>
                <div className="d-flex ms-auto py-5" >
                    <input className="form-control me-2" type="search" onChange={(e)=>{setkeyword(e.target.value)}} placeholder="Products" aria-label="Search"/>
                    <button className="btn btn-outline-success"  onClick={searchproduct}>Search</button>
                </div>
               
                <MenuPopupState/>
            </div>
        </nav>
    </>
  )
}

export default Navbar;
