import { React,useEffect, useContext, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom'; 
import noteContext from '../noteContext';
import './profile.css'
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import ListItemIcon from '@mui/material/ListItemIcon';
import HomeIcon from '@mui/icons-material/Home';
import Alert from '../elements/alert';

const Profile = () => {
    
    const navigate = useNavigate();
    const context = useContext(noteContext);
    const { isLogined, userdetails,showCart,showproductdetail,alert } = context;
    const [logged, setLogged] = useState(false);
    const [user, setuser] = useState("");
    const[cartproduct,setcartproducts]=useState([]);
    const [quantity,setquantity]=useState(1)

      
    useEffect(() => {
      const checkLoginStatus = async () => {
        const loggedIn = await isLogined();
        setLogged(loggedIn);
        if (!loggedIn) {
            navigate('/');
        }
        else {

            const fetchcartitem = async () => {
                const cartData = await showCart();
                if(cartData){
                    const products=[]
                    cartData.forEach( cart=> {
                      const fetchProductDetails = async () => {
                        const product = await showproductdetail(cart.productid);
                        product.quantity=cart.quantity
                        setcartproducts(product);
                        products.push(product); // Accumulate products
                        setcartproducts([...products]); // Update state
                      };
                      fetchProductDetails();
                    });
                }
            }
            fetchcartitem()
        }

      };
      checkLoginStatus();
    }, []);
  return (
    <> 
      <div className='profilemain'>
        <div className="homelink navbar" style={{boxShadow:" gray 3px 5px 3px"}}>
              <div className="fs-1 ms-5 mt-1 fw-medium d-flex align-items-center">
              <ListItemIcon style={{ minWidth: '30px', padding: 2 }}>
                  <ShoppingCartIcon fontSize="large" />              
              </ListItemIcon>
                    <div className=' text-secondary mb-2'>Cart</div>
              </div>
              
              <Link to="/">
                        <ListItemIcon style={{ minWidth: '30px', padding: 0 }}>
                          <HomeIcon  />
                        </ListItemIcon>
                       Home
             </Link>
        </div>
        <div className="cartitemscontainer">
          {
              cartproduct && cartproduct.length ? (
                cartproduct.map((product, index) => (
                  <div key={index} className="cartitem ">
                          <div className="d-flex">  
                                <img src={product.image[0].url} width="120px"></img>
                                <div>
                                      <h3>{product.name}</h3>
                                      <p>{product.price}</p> 
                                </div>
                          
                  
                              <div className="productcount">
                                  <button onClick={()=>{setquantity(quantity-1)}} disabled={ quantity==0} >-</button>
                                  <input type="numeric" value={quantity} readOnly />
                                  <button onClick={()=>{setquantity(quantity+1)}} >+</button>
                              </div>
                          </div>
                   </div>
                ))
              ) : (
                <div>No items found</div>
              )
          }
              
        </div>
        
      </div> 
    </>
  )
}

export default Profile
