import {React,useEffect,useContext} from 'react'
import Carousel from '../carousel'
import './product.css'
import noteContext from '../noteContext'
import { useParams } from 'react-router-dom';
import Spinner from '../spinner';


const Productitem = (props) => {
    const context=useContext(noteContext)
    const {showproductdetail,product,isLoading}=context;
    const {id}=useParams()


    const fetchproductdetail=async()=>{
        await showproductdetail(id)
      
    }

    useEffect(()=>{
        fetchproductdetail()
    },[])

  return (
    <>
        { isLoading ? (   <Spinner/> ) : (
            <div className="productitempage">
                <div className="productitembox container">
                      <div className="carouselbox">
                               <Carousel image={product.image}/>
                        
                      </div>
                      
                     <div className="productarea">
                          <div className="productname">
                                <h2>{product.name}</h2>
                                <span>Product  #{product._id}</span>
                          </div>
                          <div className="productrati">
                                <h2>{product.name}</h2>
                                <span>Product  #{product._id}</span>
                          </div>

                    </div >
                </div>
        
            </div>
        )}
    </>
  )
}

export default Productitem
