import noteContext from "./noteContext";
import React ,{useState} from 'react'



const NoteState = (props) => {
      

     //alert box 
      const[alert,setalert]=useState(null);
      const callalert=(type,msg)=>{
          setalert({type:type,
                  msg:msg
                  
          })

          setTimeout(() => {
            setalert(null)
          },1500);
      }

      // API call for register
      const userSignup=async(node)=>{

          try {
              const response = await fetch('http://localhost:5000/api/auth/signup', {
                  method: 'POST',
                  headers: {
                      'Content-Type': 'application/json'
                  },
                  body: JSON.stringify({
                      name: node.name,
                      email: node.email,
                      password: node.password
                  })
              });

              if (!response.ok)      return callalert("Error", data.error || "Something went wrong");
              const data = await response.json();
                callalert("Success", "Registration successful");
                return true;
          } catch (err) {
                callalert("Error", "Server error, please try again later");
                return false
          }

      } 


      //api call for login
  
      const userLogin=async(node)=>{
        try {
              const response = await fetch('http://localhost:5000/api/auth/login', {
                  method: 'POST',
                  headers: {
                      'Content-Type': 'application/json'
                  },
                  body: JSON.stringify({
                      email: node.email,
                      password: node.password
                  })
              });
      
              const data = await response.json();  
            if (!response.ok)     return props.callalert("Error", data.error || "Something went wrong");

              localStorage.setItem('token',data.token);
              callalert("Success", "Login successful");
              return true
          } catch (error) {
              callalert("Error", "Server error, please try again later");
              return false;
          }
      }


  return (
    <noteContext.Provider value={{userSignup,userLogin,alert,callalert}}>
            {props.children}
    </noteContext.Provider>
  )
}

export default NoteState
