import { React,useEffect, useContext, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom'; 
import noteContext from '../noteContext';
import './profile.css'
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import ListItemIcon from '@mui/material/ListItemIcon';
import HomeIcon from '@mui/icons-material/Home';
import Alert from '../elements/alert';

const Profile = () => {
    
    const navigate = useNavigate();
    const context = useContext(noteContext);
    const { isLogined, userdetails,alert } = context;
    const [logged, setLogged] = useState(false);
    const [user, setuser] = useState("");  
    useEffect(() => {
      const checkLoginStatus = async () => {
        const loggedIn = await isLogined();
        setLogged(loggedIn);
         console.log(loggedIn)
        if (loggedIn) {
          const userinfo = await userdetails();
            setuser(userinfo);
            console.log(userinfo)
        }
        else{
            navigate('/');
        }
      };
      checkLoginStatus();
    }, [isLogined]);
  return (
    <> 
      <div className='profilemain'>
        <div className="homelink navbar" style={{boxShadow:" gray 3px 5px 3px"}}>
              <div className="fs-1 ms-5 mt-2 fw-medium">
              <ListItemIcon style={{ minWidth: '30px', padding: 0 }}>
                    <ShoppingCartIcon style={{ fontSize: '20px', margin: 0 }} />
              </ListItemIcon>
                    <span className=''>Cart</span>
              </div>
              
              <Link to="/">
                        <ListItemIcon style={{ width: '30px' }}>
                          <HomeIcon  />
                        </ListItemIcon>
                       Home
             </Link>
        </div>
        
      </div> 
    </>
  )
}

export default Profile
