import noteContext from "./noteContext";
import React ,{useState} from 'react'



const NoteState = (props) => {
      
     //alert box 
      const[alert,setalert]=useState(null);
      const[allproducts,setproducts]=useState([])           //all
      const[product,setproduct]=useState({})               //single product
      const[result,setresult]=useState({})               //single product



      const [isLoading,setloading]=useState('')
      const callalert=(type,msg)=>{
          setalert({type:type,
                  msg:msg
                  
          })

          setTimeout(() => {
            setalert(null)
          },1500);
      }


    //show products
    const showProducts=async(keyword,currentpage)=>{
        setloading(true)      
        try {
             let link='http://localhost:5000/api/v1/product/getall'
            if(keyword && keyword!=="false" )  link+=`?name=${keyword}`
             if(currentpage)  link+=`?page=${currentpage}`
             const response = await fetch(link, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json'
                },
              
            });
        
        const data = await response.json();
        if (!response.ok)      return callalert("Error", data.error || "Something went wrong"); 
        console.log(data);
        setproducts(data.products)
        setresult({totalresult:data.totalresults,pagesize:data.resultperpage})
        setloading(false)
        } catch (err) {
              callalert("Error", "Server error, please try again later");
              
        }

    } 

  

    //show product detail
    const showproductdetail=async(id)=>{
        setloading(true)      
        try {
             
             const response = await fetch(`http://localhost:5000/api/v1/product/getProductDetail/${id}`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json'
                },
              
            });
        
        const data = await response.json();
        if (!response.ok)      return callalert("Error", data.error || "Something went wrong"); 
        await  setproduct(data.product)
        setloading(false)
        } catch (err) {
              callalert("Error", "Server error, please try again later");
              
        }

    } 
    




      // API call for register
      const userSignup=async(node)=>{

          try {
              const response = await fetch('http://localhost:5000/api/auth/signup', {
                  method: 'POST',
                  headers: {
                      'Content-Type': 'application/json'
                  },
                  body: JSON.stringify({
                      name: node.name,
                      email: node.email,
                      password: node.password
                  })
              });

              const data = await response.json();
              if (!response.ok)      return callalert("Error", data.error || "Something went wrong");
              callalert("Success", "Registration successful");
              return true;
              
          } catch (err) {
                callalert("Error", "Server error, please try again later");
                return false
          }

      } 


      //api call for login
  
      const userLogin=async(node)=>{
        try {
              const response = await fetch('http://localhost:5000/api/auth/login', {
                  method: 'POST',
                  headers: {
                      'Content-Type': 'application/json'
                  },
                  body: JSON.stringify({
                      email: node.email,
                      password: node.password
                  })
              });
      
            const data = await response.json();  
            if (!response.ok)     return props.callalert("Error", data.error || "Something went wrong");

              localStorage.setItem('token',data.token);
              callalert("Success", "Login successful");
              return true
          } catch (error) {
              callalert("Error", "Server error, please try again later");
              return false;
          }
      }


  return (
    <noteContext.Provider value={{showProducts,userSignup,userLogin,showproductdetail,alert,allproducts,product,isLoading,result,callalert}}>
            {props.children}
    </noteContext.Provider>
  )
}

export default NoteState
