import * as React from 'react';
import {useEffect,useContext,useState} from 'react'
import {Link,useLocation,useNavigate} from "react-router-dom"; 
import noteContext from '../noteContext';
import Button from '@mui/material/Button';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import PopupState, { bindTrigger, bindMenu } from 'material-ui-popup-state';



export default function MenuPopupState() {
    const navigate=useNavigate();
    const context=useContext(noteContext)
    const {callalert,isLogined,userLogout}=context;
    const [logged,setlogged]=useState(false);

     useEffect(()=>{
        const checkLoginStatus = async () => {
            const loggedIn = await isLogined();
            setlogged(loggedIn);
        };
        checkLoginStatus();
    },[isLogined])

    const logout=async()=>{
         await userLogout();
        console.log("hi")
        setlogged(false);
        navigate('./');

        // {!logged? <><Link className="btn btn-primary mx-2" to="http://localhost:3000/login" role="button">Login</Link>
        //     <Link className="btn btn-primary" to="http://localhost:3000/signup" role="button">SignUp</Link></>:
        //     <button className="btn btn-primary mx-2" onClick={logout}>Logout</button>
        //     }



     }
  return (
    <PopupState variant="popover" popupId="demo-popup-menu">
      {(popupState) => (
        <React.Fragment>
          <Button variant="contained" {...bindTrigger(popupState)}>
            Dashboard
          </Button>
          <Menu {...bindMenu(popupState)}    sx={{minWidth: '250px',  // Adjust width of the menu
          padding: '10px',    // Optional: Add padding inside the menu
        }}>
            <MenuItem onClick={popupState.close} sx={{ fontSize: '1.2rem', padding: '10px 20px' }} >Profile</MenuItem>
            <MenuItem onClick={popupState.close} sx={{ fontSize: '1.2rem', padding: '10px 20px' }} >Orders</MenuItem>
            <MenuItem onClick={popupState.close} sx={{ fontSize: '1.2rem', padding: '10px 20px' }} >Logout</MenuItem>
            {/* <MenuItem onClick={popupState.close}>Login</MenuItem> */}
            
          </Menu>
        </React.Fragment>
      )}
    </PopupState>
  );
}
